ruleSearch.bookList:
@js:
if (/list/.test(baseUrl)) {
org.jsoup.Jsoup.parse(result).select(".list-comic").toArray().map(li=>({
id:li.attr("data-key"),
name:li.select(".txtA").text(),
author:li.select(".info").text().replace("作者：",""),
coverUrl:li.select("img").attr("src"),
uri:li.select(".txtA").attr("href")
}));
} else if (/rank/.test(baseUrl)) {
org.jsoup.Jsoup.parse(result).select(".itemBox").toArray().map(div=>({
id:div.attr("data-key"),
name:div.select(".title").text(),
author:div.select("p:nth-of-type(1)").text(),
last_chapter_name:div.select(".date").text().trim().split("\\s+")[0],
coverUrl:div.select("img").attr("src"),
uri:div.select(".title").attr("href")
}));
} else {
JSON.parse(result).items.map(item=>{
item.uri = "https://mh.dushimh.com"+item.uri;
return item;
});
}

